def reset_quiz(user_id):
    user_answers[user_id] = []

def start_quiz(user_id, question_index, answer_index):
    if user_id not in user_answers:
        reset_quiz(user_id)
    user_answers[user_id].append(answer_index)
    return question_index + 1 < len(questions)

def get_result(user_id):
    answers = user_answers.get(user_id, [])
    scores = [0, 0, 0, 0]
    for answer in answers:
        scores[answer] += 1
    max_index = scores.index(max(scores))
    animal_list = list(animals.keys())
    selected_animal = animal_list[max_index]
    return selected_animal, animals[selected_animal]["description"], animals[selected_animal]["image_url"]
